 //10A. Java program to find area and circumference of circle without using method.

package Jsd_Assignment01;

public class AreaOfCircle10A {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		double area, circumference;
		double radius = 4;
		double PI = 3.14;
			
		area = 3.14 * radius * radius;
			
		circumference = 2 * 3.14 * radius;
			
		System.out.println("Area of circle without using methods is :" +area);
			
		System.out.println("Circumference of circle without using methods is :" +circumference);
		

	}

}

